export class Player {
  constructor(game, options) {
    this.game = game
    const {world } = game
    this.world = world
    const { x, y } = options
    this.x = x
    this.y = y
    this.width = world.unitLength * 0.8
    this.height = world.unitLength * 3
    this.vx = 0
    this.vy = 0
    this.accelerations = {
      x: [],
      y: [
        {
          type: 'gravity',
          magnitude: 0.5
        }
      ]
    }
  }

  get baseY() {
    return this.world.groundHeight - this.height
  }

  get acceleration() {
    return {
      x: this.accelerations.x.reduce((a, b) => a + b.magnitude, 0),
      y: this.accelerations.y.reduce((a, b) => a + b.magnitude, 0)
    }
  }

  get onTheGround() {
    return this.y === this.baseY
  }

  draw() {
    this.world.context.fillRect(this.x, this.y, this.width, this.height)
  }

  startMoving(direction) {
    const speed = this.world.unitLength / 10
    if (direction === 'left') return (this.vx = speed * -1)
    if (direction === 'right') return (this.vx = speed)
  }

  stopMoving() {
    this.vx = 0
  }

  jump() {
    if (this.onTheGround) return (this.vy = -20)
  }

  addAntiGravity() {
    const groundAccelertionIndex = this.accelerations.y.findIndex(
      acceleration => acceleration.type === 'ground'
    )
    if (groundAccelertionIndex === -1) {
      this.accelerations.y.push({
        type: 'ground',
        magnitude: -0.5
      })
    }
  }

  removeAntiGravity() {
    const groundAccelertionIndex = this.accelerations.y.findIndex(
      acceleration => acceleration.type === 'ground'
    )
    if (groundAccelertionIndex !== -1) {
      this.accelerations.y.splice(groundAccelertionIndex, 1)
    }
  }

  update() {
    this.x += this.vx
    if (this.x < -this.width) {
      // console.log(this.world.canvas.width)
      this.x = this.world.canvas.width
    }
    if (this.x > this.world.canvas.width) {
      this.x = -this.width
    }
    // console.log(this.x === -this.width)

    this.vy += this.acceleration.y

    const newY = this.y + this.vy
    newY < this.baseY ? (this.y = newY) : (this.y = this.baseY)
    // this.y = newY
  }
}
